mapper.select:
SELECT



FROM qiudui  a



        and a.id in

        #{item}



        and a.qiudui_uuid_number like CONCAT('%',#{params.qiuduiUuidNumber},'%')


        and a.qiudui_name like CONCAT('%',#{params.qiuduiName},'%')


        and a.qiudui_address like CONCAT('%',#{params.qiuduiAddress},'%')


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.qiudui_types = #{params.qiuduiTypes}


        and a.qiudui_shenglv like CONCAT('%',#{params.qiuduiShenglv},'%')


          and a.qiudui_clicknum >= #{params.qiuduiClicknumStart}   


          and a.qiudui_clicknum <= #{params.qiuduiClicknumEnd}   


        and a.qiudui_clicknum = #{params.qiuduiClicknum}


        and a.qiudui_content like CONCAT('%',#{params.qiuduiContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}



order by a.${params.sort} ${params.order}
